        public ActionResult PostVideo(string token, int eventId, string text)
        {
            if (Request != null && Request.InputStream != null)
            {
                byte[] bytes;
                SocialForumLogic sc = new SocialForumLogic();
                using (BinaryReader br = new BinaryReader(Request.InputStream))
                {
                    bytes = br.ReadBytes(Request.ContentLength);
                    string name = Guid.NewGuid().ToString() + ".mp4";
                    var flag = sc.PostVideo(bytes, Path.Combine(VideoSavePath, name));
                    if (flag)
                    {
                        SocialForumLogic sociallogic = new SocialForumLogic();
                        sociallogic.SaveVideo(new SocialPostMdoel
                        {
                            EventId = eventId,
                            AttendeeId = CurrentUserSession.AttendesID.Value,

                            Text = text,
                            Video = name
                        });
                        return Json(new { Data = "Success" }, JsonRequestBehavior.AllowGet);
                    }
                    else
                    {
                        return Json(new { Data = "Failed to Upload" }, JsonRequestBehavior.AllowGet);
                    }
                }
            }
            else
            {
                return Json(new { Data = "No Video Posted" }, JsonRequestBehavior.AllowGet);
            }
        }
        public ActionResult PostAudio(string token, string text)
