            else
            {
                return Json(new { Data = "No Video Posted" }, JsonRequestBehavior.AllowGet);
            }
        }


        public async Task<ActionResult> PostprofilePhotos(string token, string base64String)
        {
            Entities db = new Entities();
            var attendee = await db.Attendes.FindAsync(CurrentUserSession.Attende.ID);

            if (string.IsNullOrEmpty(base64String))
            {
                attendee.Thumbnail = null;
                await db.SaveChangesAsync();
            }
            else
            {

                string name = Guid.NewGuid().ToString() + ".png";
                attendee.Thumbnail = name;
                await db.SaveChangesAsync();


                // Convert Base64 String to byte[]
                //string base64String = "";
                byte[] imageBytes = Convert.FromBase64String(base64String);
                MemoryStream ms = new MemoryStream(imageBytes, 0,
                  imageBytes.Length);
                ms.Write(imageBytes, 0, imageBytes.Length);
                Image image = Image.FromStream(ms, true);
                image.Save(Path.Combine(ImageSavePath, name));

            }
            return Json(new { Data = "Success" }, JsonRequestBehavior.AllowGet);
        }
