        [HttpGet]
        public async Task<ActionResult> EventPost(string token, int eventId, string term = "")
        {
            try
            {
                SocialForumLogic sociallogic = new SocialForumLogic();
                var data = await sociallogic.GetEventPosts(eventId);
                DateTime now = DateTime.UtcNow;

                return Json(new ResponseModel
                {
                    Message = string.Empty,
                    StatusCode = HttpStatusCode.OK,
                    Data = data.Select(x => new
                    {
                        PostId = x.ID,
                        AttendeeID = x.AttendesID,
                        EventId = eventId,
                        PostedBy = x.Attende.FirstName,
                        PostType = x.PostType.Name,
                        Text = x.SocialForumDescription,
                        DeleteRequest = (x.DeleteRequest == true ? false : true),
                        File = x.URL == null ? null : string.Format("{0}://{1}{2}", Request.Url.Scheme, Request.Url.Authority, MvcCustom.APiImagePath(x.URL)),
                        IsVisible = (x.URL == null || x.URL == "" ? false : true),
                        Date = x.UploadON.ToString(),
                        isLiked = (x.PostUserLikes.Where(z => z.AttendesID == CurrentUserSession.AttendesID.Value).FirstOrDefault() != null ? true : false),
                        Userpic = string.Format("{0}://{1}{2}", Request.Url.Scheme, Request.Url.Authority, MvcCustom.APiImagePath(x.Attende.Thumbnail)),
                        Likes = x.PostUserLikes.Count <= 2 ? String.Join(",", x.PostUserLikes.Select(y => y.Attende.FirstName)) : x.PostUserLikes.Count.ToString(),
                        Delete = (x.DeleteRequest == true ? "Pending" : "Delete"),
                        CommentsCount = (x.ForumSocialComments.Count(y => y.PostID == x.ID) == 0 ? "0 Comments" : string.Format("{0} Comments", x.ForumSocialComments.Count(y => y.PostID == x.ID))),
                        LastComment = CreateCommentsObj(x.ForumSocialComments.OrderByDescending(y => y.CommentedON).FirstOrDefault())
                    }).ToList(),
                }, JsonRequestBehavior.AllowGet);
            }
            catch (Exception ex)
            {

                throw ex;
            }

        }

        private object CreateCommentsObj(ForumSocialComment forumSocialComment)
        {
            if (forumSocialComment == null)
            {
                return null;
            }

            return new
            {
                forumSocialComment.Attende.ID,
                forumSocialComment.Attende.FirstName,
                Userpic = string.Format("{0}://{1}{2}", Request.Url.Scheme, Request.Url.Authority, MvcCustom.APiImagePath(forumSocialComment.Attende.Thumbnail)),
                forumSocialComment.Comment,
                forumSocialComment.CommentedON
            };

        }

