        [HttpGet]
        public async Task<ActionResult> DeleteRequest(string token, int postid)
        {
            try
            {
                Entities db = new Entities();

                var update = await db.SocialForums.FindAsync(postid);
                if (update.DeleteRequest == null || update.DeleteRequest == false)
                {
                    update.DeleteRequest = true;
                    await db.SaveChangesAsync();
                    return Json(new { Data = "Success" }, JsonRequestBehavior.AllowGet);
                }
                else
                {
                    return Json(new { Data = "Error" }, JsonRequestBehavior.AllowGet);
                }
            }
            catch (Exception ex)
            {
                //ex.Message.ToString();
                return Json(new { Data = "Error" }, JsonRequestBehavior.AllowGet);
            }

        }
        /// <summary>
        /// Gives the photos of an album
        /// </summary>
        /// <param name="token"></param>
        /// <param name="albumID"></param>
        /// <returns>Retruns data about photos</returns>
        public ActionResult GetAlbumPhotos(string token, int albumID)
        {
            SocialForumLogic sociallogic = new SocialForumLogic();
            var albumPhotos = sociallogic.GetAlbumPhotos(albumID);
            if (albumPhotos == null)
            {
                return Json("Album Not found");
            }
            var jsonResult = Json(new
            {
                photos = albumPhotos.Select(x => new
                {
                    Url = string.Format("{0}://{1}{2}", Request.Url.Scheme, Request.Url.Authority, MvcCustom.APiImagePath(x.URL)),  //Helpers.ImageToBase64(MvcCustom.APiImagePath(x.URL)),
                    Description = (x.Description == null && x.Description == "" ? "No Description" : x.Description),
                    Status = x.Status,
                    Likes = x.PhotoLikes.Count,
                    IsLiked = x.PhotoLikes.Any(y => y.AttendeeID == CurrentUserSession.AttendesID),
                    x.ID
                })
            }, JsonRequestBehavior.AllowGet);
            //jsonResult.MaxJsonLength = int.MaxValue;
            return jsonResult;
        }
